
public class amazingSubarrays {
    public int solve(String A) {
        char[] charA = A.toCharArray();
        int N = A.length();

        int total = 0;
        for (int i = 0; i < N; i++) {
            if (charA[i] == 'A' || charA[i] == 'E' || charA[i] == 'I' || charA[i] == 'O' || charA[i] == 'U' || charA[i] == 'a' || charA[i] == 'e' || charA[i] == 'i' || charA[i] == 'o' || charA[i] == 'u') {
                int value = N - i;
                total += value;
            }
            total = total % 10003;
        }
        System.out.println(total);
        return total;
    }
    public static void main(String[] args) {
        amazingSubarrays az = new amazingSubarrays();
        // String A = "ABEC";
        String A =  "";
        az.solve(A);
    }
}
