'''
You have given a string A having Uppercase English letters.

You have to find how many times subsequence "AG" is there in the given string.

NOTE: Return the answer modulo 109 + 7 as the answer can be very large.
'''



class Solution:

    # From right to left
    def solveRtoL(self, A):
        N = len(A)
        count = 0
        ans = 0
        for i in range(N - 1, -1, -1):
            if A[i] == 'G':
                count += 1
            elif A[i] == 'A':
                ans = ans + count
            # print(A[i])

        return ans%1000000007

    # From left to right
    def solveLtoR(self, A):
        N = len(A)
        count = 0
        ans = 0
        for i in range(0, N):
            if A[i] == 'A':
                count += 1
            elif A[i] == 'G':
                ans = ans + count

        return ans%1000000007







A = "ABCGAG"
# A = 'GAB'
# A = 'BAAGDCAG'
# A = 'BCAGGAAG'
# A = 'ACGDGAG'
# A = ''
soln = Solution()
print(soln.solveRtoL(A))
print(soln.solveLtoR(A))
