class Solution:
    def solve(self, A):
        N = len(A)
        vowels = ['a','e','i','o','u','A','E','I','O','U']
        count = 0
        iterate = 1
        for i in range(N-1, -1, -1):
            if A[i] in vowels:
                count += iterate
                # print(i, iterate, count)
            
            iterate += 1
        
        return count % 10003



